import javax.swing.*;
import java.awt.*;
import java.awt.event.*;
import java.util.ArrayList;
import java.util.Random;
public class SnakeGame extends JPanel implements ActionListener {
private final int WIDTH = 600;
private final int HEIGHT = 600;
private final int UNIT_SIZE = 20;
private final int GAME_UNITS = (WIDTH * HEIGHT) / (UNIT_SIZE * UNIT_SIZE);
private final int DELAY = 100;
private final ArrayList<Integer> snakeX = new ArrayList<>();
private final ArrayList<Integer> snakeY = new ArrayList<>();
private int snakeLength = 1;
private int foodX;
private int foodY;
private boolean up = false;
	private boolean down = false;
	private boolean left = false;
	private boolean right = true;
	private boolean gameOver = false;
	private final Random random = new Random();
	private Timer timer;
	public SnakeGame() {
		setPreferredSize(new Dimension(WIDTH, HEIGHT));
		setBackground(Color.BLACK);
		setFocusable(true);
		addKeyListener(new MyKeyAdapter());
		startGame();
	}
		public void startGame() {
		spawnFood();
		timer = new Timer(DELAY, this);
		timer.start();
		snakeX.add(WIDTH / 2);
		snakeY.add(HEIGHT / 2);
	}
	public void spawnFood() {
		foodX = random.nextInt((WIDTH / UNIT_SIZE)) * UNIT_SIZE;
		foodY = random.nextInt((HEIGHT / UNIT_SIZE)) * UNIT_SIZE;
	}
	public void move() {
		for (int i = snakeLength - 1; i > 0; i--) {
		snakeX.set(i, snakeX.get(i - 1));
		snakeY.set(i, snakeY.get(i - 1));
	}
	if (up) snakeY.set(0, snakeY.get(0) - UNIT_SIZE);
		if (down) snakeY.set(0, snakeY.get(0) + UNIT_SIZE);
		if (left) snakeX.set(0, snakeX.get(0) - UNIT_SIZE);
		if (right) snakeX.set(0, snakeX.get(0) + UNIT_SIZE);
	}
	public void checkFoodCollision() {
		if (snakeX.get(0) == foodX && snakeY.get(0) == foodY) {
		snakeLength++;
		spawnFood();
	}
}
public void checkCollision() {
	for (int i = snakeLength - 1; i > 0; i--) {
		if (snakeX.get(0).equals(snakeX.get(i)) && snakeY.get(0).equals(snakeY.get(i))) {
		gameOver = true;
	}
}
if (snakeX.get(0) < 0 || snakeX.get(0) >= WIDTH || snakeY.get(0) < 0 || snakeY.get(0) >= HEIGHT) {
	gameOver = true;
}
		if (gameOver) {
		timer.stop();
		}
		}
		public void paintComponent(Graphics g) {
		super.paintComponent(g);
		if (!gameOver) {
		for (int i = 0; i < snakeLength; i++) {
		g.setColor(Color.GREEN);
		g.fillRect(snakeX.get(i), snakeY.get(i), UNIT_SIZE, UNIT_SIZE);
		}
		g.setColor(Color.RED);
		g.fillRect(foodX, foodY, UNIT_SIZE, UNIT_SIZE);
		} else {
		g.setColor(Color.WHITE);
		g.setFont(new Font('Ink Free', Font.BOLD, 40));
g.drawString('Game Over', WIDTH / 4, HEIGHT / 2);
		}
		}
		@Override
public void actionPerformed(ActionEvent e) {
		if (!gameOver) {
		move();
		checkFoodCollision();
		checkCollision();
		}
		repaint();
		}
private class MyKeyAdapter extends KeyAdapter {
		@Override
		public void keyPressed(KeyEvent e) {
		int key = e.getKeyCode();
		if (key == KeyEvent.VK_LEFT && !right) {
		left = true;
		up = false;
		down = false;
		}
		if (key == KeyEvent.VK_RIGHT && !left) {
		right = true;
		up = false;
		down = false;
		}
		if (key == KeyEvent.VK_UP && !down) {
		up = true;
		right = false;
		left = false;
		}
		if (key == KeyEvent.VK_DOWN && !up) {
		down = true;
		right = false;
		left = false;
		}
		}
		}
		public static void main(String[] args) {
		JFrame frame = new JFrame('Snake Game');
		SnakeGame snakeGame = new SnakeGame();
		frame.add(snakeGame);
frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
frame.pack();
frame.setVisible(true);
		}
		}
